<script>
import { ref, computed, watch } from 'vue';
import { useLanguage } from '../../stores/language';
import loaderVue from '../../Shared/ui/loaderVue.vue';
import { Link } from '@inertiajs/inertia-vue3';
import { Inertia } from '@inertiajs/inertia';
import { setCookie } from '../../services/cookie.js';


export default {
    name: "logFormVue",
    components: { Link,loaderVue },
    setup() {
        const userName = ref(null);
        const pass = ref(null);
        const visible = ref(false);
        const isLoading = ref(false);
        const errorEmpty = ref(false);
        const languageStore = useLanguage();
        const content = computed(() => languageStore.langContent);
        const currentLang = computed(() => languageStore.lang);

        watch(userName, (newValue, oldValue) => {
            if (newValue !== oldValue) {
                errorEmpty.value = false;
            }
            if (newValue === '' && newValue.length === 0) {
                errorEmpty.value = true;
            }
        });

        const changeType = () => {
            visible.value = !visible.value;
        };

        const verifyData = () => {
            isLoading.value = true;
            setTimeout(() => {
                if (userName.value === null || userName.value === '' || userName.value.length <= 0) {
                    errorEmpty.value = true;
                    isLoading.value = false;
                } else if (pass.value === null || pass.value === '' || pass.value.length <= 0) {
                    errorEmpty.value = true;
                    isLoading.value = false;
                } else {
                    setCookie('userID', 1, {secure: true, 'max-age': 3600});
                    Inertia.visit(route('settings.general'))
                    isLoading.value = false;
                }
            }, 400);
        };

        return {
            content,
            userName,
            pass,
            visible,
            isLoading,
            errorEmpty,
            currentLang,
            changeType,
            verifyData
        };
    }
}
</script>


