            <div class="login_box">
                <div class="login_box_wrapper">

                    <form class="login_form">
                        <div class="form_content">
                            <h2 class="login_title color_main font-32 bold">
                                {{ content.login }}
                            </h2>
                            <div class="login_item">
                                <div class="login_label font-16 bold">
                                    {{ content.labelUser }}
                                </div>
                                <div class="login_input">
