<script>
import { ref, computed, watch } from 'vue';
import { useLanguage } from '../../stores/language';
import { Link } from '@inertiajs/inertia-vue3';
import loaderVue from '../ui/loaderVue.vue';

export default {
  name: "recoverFormVue",
  components: { Link,loaderVue },
  setup() {
    const content = {
      'RU': {
        title: 'Восстановление пароля',
        labelUser: 'Логин или Email',
        logIn: 'Восстановить пароль',
        passRecover: 'Я вспомнил пароль',
        delimiter: 'или',
        error: 'Необходимо заполнить'
      },
      'RO': {
        title: 'Recuperarea parolei',
        labelUser: 'Autentificare sau e-mail',
        logIn: 'Restaurați parola',
        passRecover: 'Mi-am amintit parola.',
        delimiter: 'sau',
        error: 'Trebuie să completați'
      },
      'EN': {
        title: 'Password recovery',
        labelUser: 'Login or Email',
        logIn: 'Restore password',
        passRecover: 'I remembered the password',
        delimiter: 'or',
        error: 'You need to fill out'
      }
    };

    const userName = ref(null);
    const isLoading = ref(false);
    const errorEmpty = ref(false);

    const languageStore = useLanguage();
    const currentLang = computed(() => languageStore.lang);

    watch(userName, (newValue, oldValue) => {
      if (newValue !== oldValue) {
        errorEmpty.value = false;
      }
      if (newValue === '' && newValue.length === 0) {
        errorEmpty.value = true;
      }
    });

    const verifyData = () => {
      isLoading.value = true;
      setTimeout(() => {
        if (userName.value === null || userName.value === '' || userName.value.length <= 0) {
          errorEmpty.value = true;
          isLoading.value = false;
        } else {
          alert('Recovery success!');
          isLoading.value = false;
        }
      }, 400);
    };

    return {
      content,
      userName,
      isLoading,
      errorEmpty,
      currentLang,
      verifyData
    };
  }
}
</script>

