<style>
.login{
  background: linear-gradient(90deg, #edf4fb 50%, #fff 0);
  padding-right: calc(10px - 100vw + 100%);
}
.login_img{
  align-items: center;
  background-color: transparent;
  display: flex;
  height: 100%;
  justify-content: center;
}
.login_box{
  background-color: #fff;
  display: flex;
  flex-direction: column;
  height: 100%;
  justify-content: center;
  padding: 100px 38px 123px 49px;
  position: relative;
  width: 100%;
}

.login_title{
  margin: 0 0 28px;
}

.login_label{
  margin-bottom: 4px;
}

.login_item{
  margin-bottom: 20px;
}

.login_input{
  position: relative;
}

.login_box_wrapper{
  max-width: 456px;
  width: 100%;
}

.login_eye{
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  cursor: pointer;
  right: 16px;
  height: 20px;
}

.login_eye svg{
  fill: #939699;
  width: 20px;
  height: 20px;
}

.login_icon_err svg{
  fill: #fa4a4a;
}

.delimiter{
  position: relative;
  text-align: center;
  width: 100%;
  margin-bottom: 16px;
}
.delimiter span{
  background-color: #fff;
  color: #000;
  font-size: 16px;
  font-weight: 400;
  padding: 0 10px;
  position: relative;
  z-index: 1;
}
.delimiter::after{
  background-color: #c9cbcc;
  content: "";
  height: 1px;
  left: 0;
  position: absolute;
  top: 50%;
  width: 100%;
}
.recovery_link{
  display: block;
  margin-bottom: 20px;
}
.recovery_link_wrapper{
  gap: 5px;
}

.login_err{
  color: #d22222;
  margin-top: 5px;
  display: none;
}
.login_err{
  display: block;
}

.log_input.active{
  border-color: #fa4a4a;
}

</style>
