<script>
import { ref, computed, watch } from 'vue';
import { useLanguage } from '../../stores/language';
import { Link } from '@inertiajs/inertia-vue3';
import loaderVue from '../ui/loaderVue.vue';

export default {
  name: "registerFormVue",
  components: { Link, loaderVue },
  setup() {
    const content = {
      'RU': {
        title: 'Присоединяйтесь к RemOnline!',
        labelUser: 'Email',
        labelPass: 'Пароль',
        delimiter: 'или',
        logIn: 'Начать бесплатно',
        register: 'Уже есть аккаунт?',
        registerLink: 'Авторизоваться',
        regCondition: 'Продолжая, вы соглашаетесь с нашими условиями',
        license: 'Лицензионного договора',
        and: 'и',
        policy: 'Политикой конфиденциальности',
        error: 'Необходимо заполнить'
      },
      'RO': {
        title: 'Alăturați-vă de RemOnline!',
        labelUser: 'E-mail',
        labelPass: 'Parola',
        delimiter: 'sau',
        logIn: 'Începeți gratuit',
        register: 'Aveți deja un cont??',
        registerLink: 'Autentificare',
        regCondition: 'Continuând, sunteți de acord cu termenii și condițiile noastre.',
        license: 'Acord de licență',
        and: 'și',
        policy: 'Politica de confidențialitate',
        error: 'Trebuie să completați'
      },
      'EN': {
        title: 'Join to RemOnline!',
        labelUser: 'Email',
        labelPass: 'Password',
        delimiter: 'or',
        logIn: 'Get started for free',
        register: 'Already have an account?',
        registerLink: 'Authorize',
        regCondition: 'By continuing, you agree to our terms and conditions',
        license: 'License agreement',
        and: 'and',
        policy: 'Privacy policy',
        error: 'You need to fill out'
      }
    };

    const userName = ref(null);
    const pass = ref(null);
    const visible = ref(false);
    const isLoading = ref(false);
    const errorEmpty = ref(false);

    const languageStore = useLanguage();
    const currentLang = computed(() => languageStore.lang);

    watch(userName, (newValue, oldValue) => {
      if (newValue !== oldValue) {
        errorEmpty.value = false;
      }
      if (newValue === '' && newValue.length === 0) {
        errorEmpty.value = true;
      }
    });

    const changeType = () => {
      visible.value = !visible.value;
    };

    const verifyData = () => {
      isLoading.value = true;
      setTimeout(() => {
        if (userName.value === null || userName.value === '' || userName.value.length <= 0) {
          errorEmpty.value = true;
          isLoading.value = false;
        } else if (pass.value === null || pass.value === '' || pass.value.length <= 0) {
          errorEmpty.value = true;
          isLoading.value = false;
        } else {
          alert('Logged success!');
          isLoading.value = false;
        }
      }, 400);
    };

    return {
      content,
      userName,
      pass,
      visible,
      isLoading,
      errorEmpty,
      currentLang,
      changeType,
      verifyData
    };
  }
}
</script>

